port.findSerialPortChannel(process.env.MAC_ADDRESS, channel => {
  console.log("found serial port channel:", channel);
  port.connect(process.env.MAC_ADDRESS, channel, () => {
    console.log("connected");

    port.write(createBufferFromMessage(139, Buffer.from("00b4010000", "hex")), (err, bytesWritten) => {
      console.log("Sent command 1", err, bytesWritten);
    })

    // port.write(createBufferFromMessage(139, Buffer.from("", "hex")), (err, bytesWritten) => {
    port.write(createBufferFromMessage(139, Buffer.from("", "hex")), (err, bytesWritten) => {
        console.log("Sent command 2", err, bytesWritten);
    })

    port.write(createBufferFromMessage(139, Buffer.from("", "hex")), (err, bytesWritten) => {
      console.log("Sent command 3", err, bytesWritten);
    })

    port.on('data', function (buffer) {
      console.log("Received data: ", buffer.toString("hex"));
    });
  }, () => {
    console.log("cannot connect");
  });
}, () => {
  console.log("found nothing");
});
